#-----------------创建表--- kj_meal_shop
DROP TABLE IF EXISTS `{DB_PRE}meal_shop`;
CREATE TABLE IF NOT EXISTS `{DB_PRE}meal_shop` (
`shop_id` int(10) NOT NULL auto_increment,`shop_name` varchar(50) NOT NULL COMMENT '店铺名称',`shop_user_id` int(10) NOT NULL COMMENT '用户id',`shop_addtime` int(10) NOT NULL COMMENT '添加时间',`shop_updatetime` int(10) NOT NULL COMMENT '修改时间',`shop_intro` text NOT NULL COMMENT '介绍',`shop_linkname` varchar(50) NOT NULL COMMENT '联系人',`shop_linktel` varchar(50) NOT NULL COMMENT '联系人电话',`shop_tel` varchar(20) NOT NULL COMMENT '店铺电话',`shop_address` varchar(100) NOT NULL COMMENT '店铺地址',`shop_area_id` int(10) NOT NULL COMMENT '地区id',`shop_area_allid` varchar(50) NOT NULL COMMENT '地区id全路径',`shop_area` varchar(50) NOT NULL COMMENT '所在区域名',`shop_oneminleast` decimal(10,2) NOT NULL COMMENT '单份最低价',`shop_dispatch_price` decimal(10,2) NOT NULL COMMENT '起送价格',`shop_state` smallint(2) NOT NULL COMMENT '状态',`shop_pic_small` varchar(100) NOT NULL COMMENT '店面logo',`shop_pic` varchar(100) NOT NULL COMMENT '小图',`shop_mode` smallint(2) NOT NULL COMMENT '经营范围',`shop_extend` text NOT NULL,`shop_ticket` tinyint(1) NOT NULL COMMENT '供提发票',`shop_progress` varchar(50) NOT NULL COMMENT '店铺进度',`shop_hits` int(10) NOT NULL COMMENT '店铺访问量',`shop_sold` int(10) default '0' COMMENT '总销量',`shop_menu_num` int(10) NOT NULL COMMENT '店铺菜品数',`shop_isdel` tinyint(1) default '0' COMMENT '回收站',`shop_rebate` int(2) NOT NULL COMMENT '销售返点',`shop_checkout_date` date NOT NULL COMMENT '上次结款日',`shop_checkout_money` int(10) NOT NULL COMMENT '最低结款额',`shop_sms` smallint(2) NOT NULL COMMENT '信短通知级别',`shop_sms_tel` varchar(100) NOT NULL COMMENT '订餐短信电话',`shop_printinfo` text NOT NULL COMMENT '打印格式信息',`shop_support_score` tinyint(1) default '0' COMMENT '是否支持积分兑换',`shop_tj` int(5) default '0' COMMENT '推荐',`shop_jian` varchar(20) NOT NULL COMMENT '拼音简写',`shop_desc` varchar(500) NOT NULL COMMENT '简介',`shop_addprice` int(10) default '0' COMMENT '送配费',PRIMARY KEY (`shop_id`)
) ENGINE=myisam  DEFAULT CHARSET=utf8 AUTO_INCREMENT=6

